changeSet.sql:
create table if not exists persons_to_persons
(
id serial primary key unique,
par_id integer not null,
par_id_crush integer not null,
likes boolean default true,
created_at timestamp default now(),
updated_at timestamp default now(),
UNIQUE (par_id, par_id_crush)
);
DROP SEQUENCE IF EXISTS persons_to_persons_id_seq CASCADE;

CREATE SEQUENCE persons_to_persons_id_seq START WITH 4 INCREMENT BY 1;
